import React, { useEffect, useState, useRef } from "react";
import { StyledBottomBar } from "./styles";
import { FaFacebookSquare } from "react-icons/fa";
import { GrInstagram } from "react-icons/gr";
import { CgMail } from "react-icons/cg";
import { BiPhoneCall } from "react-icons/bi";
import { StyledIcon } from "../BottomBar/styles";

export const BottomBar = () => {
  const style = { fontSize: "180%", transition: "font-size 0.5s" };

  const onMouseLeave = (event) => (event.target.style.fontSize = "180%");

  const [hover, setHover] = useState(false);
  const iconRef = useRef(null);

  useEffect(
    (hover: boolean) => {
      if (hover) {
        iconRef.current.style.fontSize = "250%";
      } else {
        iconRef.current.style.fontSize = "180%";
      }
    },
    [hover]
  );

  enum TYPE {
    MAIL = "mail",
    PHONE = "phone",
  }

  const handleIconSwitch = (TYPE) => {
    switch (TYPE) {
      case "mail": {
        return (
          <CgMail
            style={style}
            ref={iconRef}
            onMouseEnter={() => {
              setHover(true);
            }}
          />
        );
      }
    }
  };
  return (
    <StyledBottomBar>
      <StyledIcon>
        <FaFacebookSquare
          style={style}
          onMouseEnter={({ target }) => (target.style.fontSize = "250%")}
          onMouseLeave={onMouseLeave}
        />
      </StyledIcon>
      <StyledIcon>
        <GrInstagram style={style} />
      </StyledIcon>
      <StyledIcon style={style} type="mail">
        <CgMail />
      </StyledIcon>
      <StyledIcon style={style} type="phone">
        <BiPhoneCall />
      </StyledIcon>
    </StyledBottomBar>
  );
};
