import React from "react";
import { StyledBottomBar, StyledIcon } from "./styles";
import { FaFacebookSquare } from "react-icons/fa";
import { GrInstagram } from "react-icons/gr";
import { CgMail } from "react-icons/cg";
import { BiPhoneCall } from "react-icons/bi";

export const BottomBar = () => {
  const style = { fontSize: "180%", transition: "font-size 0.5s" };

  // const onMouseEnter = (target: any) => {
  //   target.style.fontSize = "180%";
  // };

  const onMouseLeave = (event) => (event.target.style.fontSize = "180%");

  return (
    <StyledBottomBar>
      <StyledIcon>
        <FaFacebookSquare
          style={style}
          onMouseEnter={({ target }) => (target.style.fontSize = "250%")}
          // onMouseLeave={({ target }) => (target.style.fontSize = "180%")}
          onMouseLeave={onMouseLeave}
        />
      </StyledIcon>
      <StyledIcon>
        <GrInstagram style={style} />
      </StyledIcon>
      <StyledIcon>
        <CgMail style={style} />
      </StyledIcon>
      <StyledIcon>
        <BiPhoneCall style={style} />
      </StyledIcon>
    </StyledBottomBar>
  );
};
