import { createStore } from "redux";
import { PayloadAction } from "@reduxjs/toolkit";

const loginReducer = (
  state = { loggedIn: false },
  action: PayloadAction<boolean>
) => {
  if (action.type === "login") {
    return {
      loggedIn: true,
    };
  } else {
    return state;
  }
};

export const store = createStore(loginReducer);
